/* jslint latedef:false */
'use strict';

angular.module('tangHelper.executorCtrl', [])
    .controller('executorCtrl', executorCtrl);

function executorCtrl($scope, conf, connect) {
    /* jshint validthis: true */
    console.log('config: %o', conf);
    this.conf = conf;

    var status = [
        'waitingForConnecting',
        'operating',
        'watingForDisconnecting'
    ];
    //进入等待连接状态
    this.activeStatus = status[0];

    connect(conf.minerDefaultAddress).then(function(connectStatistic) {
        console.log('连接成功，%o', connectStatistic);
    });
}