var app = angular.module('tangHelper.settings', []);

app.controller('settings', settings);

function settings(getLocalIP) {
    var self = this;
    this.config = {
        type: 'asic',
        pool1URL: 'us1.ghash.io',
        pool1Port: 3333,
        pool2URL: 'us1.ghash.io',
        pool2Port: 3334,
        username: 'my',
        prefix: null,
        mask: '255.255.255.0'
    };
    this.minerDefaultIP = {
        asic: '192.168.0.254',
        ant: '192.168.0.100'
    };

    getLocalIP().then(function(IPItems) {
        self.config.ips = IPItems;
    })
}