'use strict';

angular.module('tangHelper.filter', [])
    .filter('translate', function() {
        var statusTransaltion = {
            waitingForConnecting: '等待连接矿机',
            operating: '操作中',
            waitingForDisconnecting: '等待断开连接'
        };
        return function(input) {
            return statusTransaltion[input] ? statusTransaltion[input] : input;
        };
    });